import "./App.css";
import { useState, useEffect } from "react";
import { socket } from "./socket";
import { Flex, Box, Text, Input, Button, VStack } from "@chakra-ui/react";

const IMAGEBASE64 =
  "";
function App() {
  const [username, setUsername] = useState("");
  const [jobs, setJobs] = useState([]);
  const [isConnected, setIsConnected] = useState(false);

  const handleJoin = () => {
    if (username === "") {
      return;
    }
    socket.auth = {
      username: username,
    };
    socket.connect();
  };

  const handleSubmitJob = async ({ imageBase64, width, height }) => {
    try {
      const res = await fetch("http://localhost:3001/imgJob/resize-job", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ imageBase64, width, height }),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.error || "Failed to submit job.");

      const { jobId } = data;

      // ensure we have a connection before emitting!
      if (socket.connected) {
        socket.emit("subscribeToJob", { jobId });
        setJobs((prev) => [...prev, { jobId, status: "pending" }]);
      } else {
        socket.once("connect", () => {
          socket.emit("subscribeToJob", { jobId });
          setJobs((prev) => [...prev, { jobId, status: "pending" }]);
        });
      }
    } catch (err) {
      console.log(err.message);
    }
  };
  useEffect(() => {
    const onConnect = () => {
      console.log("Socket connected.");
      setIsConnected(true);
    };

    const onDisconnect = () => {
      console.log("Socket disconnected.");
      setIsConnected(false);
    };

    const onJobComplete = ({
      jobId,
      status,
      from,
      attemptsMade,
      maxAttempts,
      failedReason,
    }) => {
      setJobs((prevJobs) =>
        prevJobs.map((job) =>
          job.jobId === jobId
            ? { jobId, status, from, attemptsMade, maxAttempts, failedReason }
            : job
        )
      );
    };

    socket.on("connect", onConnect);
    socket.on("disconnect", onDisconnect);
    socket.on("job:completed", onJobComplete);
    socket.on("job:failed", onJobComplete);

    return () => {
      socket.off("connect", onConnect);
      socket.off("disconnect", onDisconnect);
      socket.off("job:completed", onJobComplete);
    };
  }, []);
  return (
    <Flex h="100vh" direction="column" align="center" justify="center" gap={4}>
      <Text>Am I Connected: {isConnected ? "yes" : "no"}</Text>
      {!isConnected ? (
        <Box maxW="md" w="full">
          <Input
            placeholder="Input your username here."
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <Button width="auto" onClick={handleJoin} m={4}>
            Join
          </Button>
        </Box>
      ) : (
        <Box maxW="md" w="full">
          {jobs.map((item, key) => {
            return (
              <Text key={key}>
                {item.jobId}-{item.status}-{item.attemptsMade}/
                {item.maxAttempts} - {item.failedReason}
              </Text>
            );
          })}
          <Button
            width="auto"
            onClick={() =>
              handleSubmitJob({
                imageBase64: IMAGEBASE64,
                width: 300,
                height: 300,
              })
            }
            m={4}
          >
            Submit Job
          </Button>
        </Box>
      )}
    </Flex>
  );
}

export default App;
